// Get all incoming items from previous nodes
const items = $input.all();

return items.map(item => {
  const data = item.json;

  // 1. Remove null or undefined values
  Object.keys(data).forEach(key => {
    if (data[key] === null || data[key] === undefined) {
      delete data[key];
    }
  });

  // 2. Standardize text fields
  if (data.text) {
    data.text = data.text.trim();
  }

  if (data.description) {
    data.description = data.description
      .trim()
      .replace(/\s+/g, ' ');
  }

  // 3. Add cleaned timestamp
  data.cleaned_date = new Date().toISOString();

  // Return cleaned item
  return { json: data };
});

const collectedAt = new Date().toISOString();

// Hugging Face /rows response shape:
// { rows: [ { row: { text, ade, term_PT, ... }, ... }, ... ] }

const rows = items?.[0]?.json?.rows || [];
const out = [];

for (let i = 0; i < rows.length; i++) {
  const r = rows[i]?.row || {};

  const text = (r.text ?? "").toString().trim();
  const ade = (r.ade ?? "").toString().trim();
  const termPT = (r.term_PT ?? "").toString().trim();

  // Basic quality filter
  if (!text) continue;

  out.push({
    json: {
      record_id: `CADEC-${i + 1}`,
      source: "cadec_huggingface",
      event_term: termPT || ade || "unknown",
      meddra_code: null,
      severity: "unknown",
      patient_age: null,
      sex: null,
      drug_name: null,
      indication: null,
      raw_text: text,
      event_date: null,
      url: "https://huggingface.co/datasets/KevinSpaghetti/cadec",
      collected_at: collectedAt,

      // Optional: keep original fields (helps debugging + documentation)
      cadec_ade: ade || null,
      cadec_term_pt: termPT || null
    }
  });
}

// Keep your record count clean (optional cap)
return out.slice(0, 80);

