// Enhanced AI analysis - PRESERVE ALL ORIGINAL FIELDS + ENRICH
const items = $input.all();
const originalItems = $('Code in JavaScript').all();

console.log(`Processing ${items.length} items for enrichment`);
console.log(`Original items available: ${originalItems.length}`);

return items.map((item, index) => {
  const data = item.json;

  // âœ… GET ORIGINAL DATA from "Code in JavaScript" node (before AI)
  const originalData = originalItems[index]?.json || {};

  // Parse AI response
  let aiAnalysis = {};
  try {
    if (data.output && data.output[0] && data.output[0].content && data.output[0].content[0]) {
      const textContent = data.output[0].content[0].text;
      aiAnalysis = {
        severity_score: textContent.SEVERITY_SCORE || 0,
        medical_category: textContent.MEDICAL_CATEGORY || "unknown",
        urgency_level: textContent.URGENCY_LEVEL || "routine",
        recommended_action: textContent.RECOMMENDED_ACTION || "Review manually",
        confidence_level: textContent.CONFIDENCE_LEVEL || "low"
      };
    } else {
      aiAnalysis = {
        severity_score: 0,
        medical_category: "unknown",
        urgency_level: "routine",
        recommended_action: "Review manually - AI parse failed",
        confidence_level: "low"
      };
    }
  } catch (e) {
    aiAnalysis = {
      severity_score: 0,
      medical_category: "unknown",
      urgency_level: "routine",
      recommended_action: "Review manually - error",
      confidence_level: "low"
    };
  }

  // Detect source
  const source = originalData.source || 'unknown';

  // âœ… ADD ENRICHMENT based on source
  let sourceType = '';
  let sourceSummary = '';
  let description = '';

  if (source.includes('cadec')) {
    sourceType = 'Patient Report';
    sourceSummary = `Patient-reported: ${originalData.event_term || 'symptoms'}`;
    description = `Patient reported ${originalData.event_term || 'adverse event'}: ${originalData.raw_text?.substring(0, 150) || ''}`;
  } else if (source === 'pubmed') {
    sourceType = 'Research Literature';
    sourceSummary = `Research: ${originalData.pubmed_title?.substring(0, 80) || 'Article'}`;
    description = `${originalData.pubmed_title || 'Research article'}. Journal: ${originalData.pubmed_journal || 'Unknown'}. Authors: ${originalData.pubmed_authors || 'Not listed'}.`;
  } else if (source.includes('fda') || source.includes('medwatch')) {
    sourceType = 'FDA Regulatory Alert';
    sourceSummary = `FDA Safety Alert: ${originalData.fda_alert_title?.substring(0, 80) || 'Alert'}`;
    description = `FDA: ${originalData.fda_alert_title || 'Safety alert'}`;
  }

  // âœ… MERGE: Keep ALL original fields + add AI analysis + add enrichment
  const enrichedData = {
    ...originalData,  // âœ… All original fields

    // Add enrichment
    source_type: sourceType,
    source_summary: sourceSummary,
    description: description,

    // Add source-specific fields
    cadec_patient_description: source.includes('cadec') ? originalData.raw_text : undefined,
    cadec_event: source.includes('cadec') ? originalData.event_term : undefined,

    // Add AI Analysis
    ai_severity_score: aiAnalysis.severity_score,
    ai_category: aiAnalysis.medical_category,
    ai_urgency: aiAnalysis.urgency_level,
    ai_action: aiAnalysis.recommended_action,
    ai_confidence: aiAnalysis.confidence_level,
    ai_processed_at: new Date().toISOString()
  };

  // Remove undefined values
  Object.keys(enrichedData).forEach(key => {
    if (enrichedData[key] === undefined) {
      delete enrichedData[key];
    }
  });

  if (index === 0) {
    console.log('âœ… Sample enriched output:', JSON.stringify({
      record_id: enrichedData.record_id,
      source: enrichedData.source,
      source_type: enrichedData.source_type,
      pubmed_title: enrichedData.pubmed_title,
      ai_severity_score: enrichedData.ai_severity_score
    }, null, 2));
  }

  return { json: enrichedData };
});

// Get all urgent items
const items = $input.all();

if (items.length === 0) {
  return [{
    json: {
      content: "âœ… No urgent cases detected."
    }
  }];
}

// Build compact summary message
let msg = `ðŸš¨ **URGENT ALERTS** (${items.length} total)\n`;
msg += `â° ${new Date().toLocaleString()}\n\n`;
msg += `**Top 5 Critical Cases:**\n`;

// Get top 5 by severity
const top = items
  .sort((a, b) => (b.json.ai_severity_score || 0) - (a.json.ai_severity_score || 0))
  .slice(0, 5);

// Format each case
top.forEach((item, i) => {
  const d = item.json;
  msg += `${i+1}. Severity ${d.ai_severity_score}/5 | ${d.ai_category} | ${d.ai_action}\n`;
});

if (items.length > 5) {
  msg += `\n+${items.length - 5} more cases require review`;
}

msg += `\n\nâš¡ **REVIEW REQUIRED**`;

// CRITICAL: Return with content field
return [{ 
  json: { 
    content: msg 
  } 
}];

const items = $input.all();

return items.map(item => {
  const data = item.json;

  // Check if we have VALID AI output
  const hasValidOutput = !!(
    data.output && 
    data.output[0] && 
