});

// Dynamic limiting with SHUFFLE to mix all sources
const webhookData = $('Webhook').first().json.body || $('Webhook').first().json;
const requestedCount = parseInt(webhookData.record_count) || 10;

console.log('Requested:', requestedCount, 'records');

// Get all items from previous node
const allItems = $input.all();

console.log('Total items available:', allItems.length);

// âœ… SHUFFLE to mix CADEC, FDA, and PubMed records
const shuffled = allItems.sort(() => Math.random() - 0.5);

// Return only requested number from shuffled array
const limitedItems = shuffled.slice(0, requestedCount);

// Count by source
const sources = {};
limitedItems.forEach(item => {
  const source = item.json.source || 'unknown';
  sources[source] = (sources[source] || 0) + 1;
});

console.log('Returning:', limitedItems.length, 'records');
console.log('Source distribution:', JSON.stringify(sources));

return limitedItems;

// Parse PubMed summary response to get actual article data
const response = items[0].json;
const result = response.result || {};
const out = [];

// Get all article UIDs
const uids = result.uids || [];

console.log(`Processing ${uids.length} PubMed articles`);

// Process each article
uids.forEach(uid => {
  const article = result[uid];

  if (article && article.title) {
    // Extract author names with proper handling
    const authors = article.authors || [];
    const authorNames = authors
      .slice(0, 3)
      .map(a => a.name)
      .join(', ');

    const moreAuthors = authors.length > 3 ? ` +${authors.length - 3} more` : '';
    const fullAuthors = authorNames + moreAuthors;

    // Build comprehensive raw_text
    const rawText = `${article.title}. Published in ${article.fulljournalname || article.source || 'Unknown Journal'} (${article.pubdate || 'Date unknown'}). Authors: ${fullAuthors || 'Not specified'}.`;

    out.push({
      json: {
        record_id: `PUBMED-${uid}`,
        source: 'pubmed',  // âœ… CRITICAL: Must be exactly 'pubmed'
        event_term: article.title,  // Use title as event
        meddra_code: null,
        severity: 'unknown',
        patient_age: null,
        sex: null,
        drug_name: null,
        indication: null,
        raw_text: rawText,
        event_date: article.pubdate || null,
        url: `https://pubmed.ncbi.nlm.nih.gov/${uid}/`,
        collected_at: new Date().toISOString(),

        // âœ… PubMed-specific enrichment (REQUIRED for Streamlit display)
        pubmed_id: uid,
        pubmed_title: article.title,
        pubmed_journal: article.fulljournalname || article.source || 'Unknown Journal',
        pubmed_date: article.pubdate || null,
        pubmed_authors: fullAuthors || 'Not specified',
        pubmed_doi: article.elocationid || null,
        pubmed_pmcid: article.pmcid || null
      }
    });
  }
});

console.log(`âœ… Successfully parsed ${out.length} PubMed articles with full metadata`);

return out;

// Prepare complete response for Streamlit with proper IDs and full PubMed data
const allRecords = $input.all();

// Shuffle records to mix sources
const shuffled = allRecords.sort(() => Math.random() - 0.5);

// Build response object
const response = {
  success: true,
  total_records: shuffled.length,
  records: shuffled.map((item, index) => {
    const d = item.json;

    // Generate proper record ID
    let recordId = d.record_id || d.id || `RECORD-${index + 1}`;

    // If still generic, try to make it source-specific
    if (recordId === 'unknown' || !recordId) {
      const source = d.source || 'UNKNOWN';
      if (source.includes('cadec')) {
        recordId = `CADEC-${index + 1}`;
      } else if (source.includes('fda') || source.includes('medwatch')) {
        recordId = `FDA-${index + 1}`;
      } else if (source.includes('pubmed') || source === 'pubmed') {
        recordId = d.pubmed_id ? `PUBMED-${d.pubmed_id}` : `PUBMED-${index + 1}`;
      } else {
        recordId = `REC-${index + 1}`;
      }
    }

    return {
      record_id: recordId,
      source: d.source || 'unknown',
      source_type: d.source_type || null,
      source_summary: d.source_summary || null,
      event_term: d.event_term || 'unknown',
      description: d.description || null,
      raw_text: d.raw_text || '',

      // AI Analysis
      ai_severity_score: d.ai_severity_score || 0,
      ai_category: d.ai_category || 'unknown',
      ai_urgency: d.ai_urgency || 'routine',
      ai_action: d.ai_action || 'No action specified',
      ai_confidence: d.ai_confidence || 'low',

      // âœ… CRITICAL: Preserve ALL PubMed fields
      pubmed_id: d.pubmed_id || null,
      pubmed_title: d.pubmed_title || null,
      pubmed_journal: d.pubmed_journal || null,
      pubmed_authors: d.pubmed_authors || null,
      pubmed_date: d.pubmed_date || null,
      pubmed_doi: d.pubmed_doi || null,

      // CADEC fields
      cadec_patient_description: d.cadec_patient_description || null,
      cadec_event: d.cadec_event || null,

      // FDA fields
      fda_alert_title: d.fda_alert_title || null,
      fda_alert_link: d.fda_alert_link || null,

      // Metadata
      url: d.url || null,
      collected_at: d.collected_at || null
    };
  }),
  summary: {
    total: shuffled.length,
    by_source: {
      cadec: shuffled.filter(r => (r.json.source || '').includes('cadec')).length,
      fda: shuffled.filter(r => (r.json.source || '').includes('fda') || (r.json.source || '').includes('medwatch')).length,
      pubmed: shuffled.filter(r => r.json.source === 'pubmed').length
    },
    by_severity: {
      critical: shuffled.filter(r => (r.json.ai_severity_score || 0) >= 4).length,
      moderate: shuffled.filter(r => (r.json.ai_severity_score || 0) === 3).length,
      mild: shuffled.filter(r => (r.json.ai_severity_score || 0) <= 2).length
    }
  },
  outputs: {
    discord_critical_sent: shuffled.filter(r => (r.json.ai_severity_score || 0) >= 4).length,
    discord_urgent_sent: shuffled.filter(r => ['urgent', 'immediate'].includes(r.json.ai_urgency)).length > 0,
    email_sent: true,
    email_recipient: "manishasahu2055@gmail.com"
  },
  metadata: {
    processed_at: new Date().toISOString()
  }
};

console.log(`âœ… Prepared ${response.records.length} records for Streamlit`);
console.log(`ðŸ“Š Sources: CADEC=${response.summary.by_source.cadec}, FDA=${response.summary.by_source.fda}, PubMed=${response.summary.by_source.pubmed}`);
console.log(`ðŸ“„ PubMed records with titles: ${response.records.filter(r => r.pubmed_title).length}`);

return [{ json: response }];

// Format FDA RSS items into proper records - Clean, professional values
const items = $input.all();

console.log(`Formatting ${items.length} FDA items`);

return items.map((item, index) => {
  const data = item.json;

  // FDA RSS data comes in as flattened structure
  // Extract the actual RSS item data
  const rssItem = data['rss.channel.item'] || data;

  const title = rssItem.title || data.title || 'FDA Medical Device Safety Alert';
  const link = rssItem.link || data.link || 'https://www.fda.gov/';
  const description = rssItem.description || data.description || 'Medical device safety communication from FDA';
  const pubDate = rssItem.pubDate || data.pubDate || new Date().toISOString();

  return {
    json: {
      record_id: `FDA-${String(index + 1).padStart(4, '0')}`,
      source: 'fda_medwatch',
      event_term: title,
      raw_text: `${title}. ${description}`,
      event_date: pubDate,
      url: link,
      collected_at: new Date().toISOString(),

      // FDA-specific fields
      fda_alert_title: title,
      fda_alert_link: link,
      fda_pub_date: pubDate,
      fda_description: description
    }
  };
});
